import React, { PureComponent } from 'react';
import { TouchableOpacity } from 'react-native';
import { Body, Text, Thumbnail, View } from 'native-base';
import { TimeAgo } from './time'

export class Touch extends PureComponent {
    constructor(props) {
        super(props)
        this.data = { ...this.props.data }
    }
    _handlePress = () => {
        const { url, title,source } = this.data
        this.props.onPress({ url, title })
    }

    render() {
        return (
            <TouchableOpacity onPress={this._handlePress} style={{ flexDirection: 'row' }} focusedOpacity={.1} activeOpacity={.5}>
                <Thumbnail style={{ backgroundColor: '#eee', alignSelf: 'center' }} square large source={{ cache:'force-cache', uri: this.data.urlToImage ? `https://newsfeeder-hgznzarfvd.now.sh/?file=${this.data.urlToImage}&quality=20` : 'data:image/png;base64,' }} />
                <Body>
                    <Text style={{ fontFamily: 'RobotoSlab', fontSize: 16 }} numberOfLines={2}>{this.data.title}</Text>
                    <Text note numberOfLines={3}>{this.data.description}</Text>
                    <View style={{ flex: 1, flexDirection: 'row', marginTop: 8, marginLeft: 8 }}>
                        <Text note>{this.data.source.name}</Text>
                        <TimeAgo time={this.data.publishedAt} />
                    </View>
                </Body>
            </TouchableOpacity>
        )

    }

}